import os, sys, shutil, getpass


def main():
    # if sys.platform != "linux":
    #     raise Exception("Must be ran on linux")
    # if os.geteuid() != 0: # type: ignore
    #     raise Exception("Must be ran as sudo")
    showStartMenu()
    
    print(prependHome("Desktop"))

    if not os.path.exists(prependHome("Desktop/backups")):
        os.mkdir(prependHome("Desktop/backups"))
    
    shutil.copy("/etc/passwd", prependHome("Desktop/backups/passwd.back"))
    print("Backed up passwd")
    shutil.copy("/etc/group", prependHome("Desktop/backups/group.back"))
    print("Backed up group")

    handleUsers()

def handleUsers():
    userfile = open(prependHome("Desktop/users.txt"), 'r')
    needed_users = userfile.read()
    print(needed_users)
    userfile.close()


def prependHome(path: str):
    sudo_user = os.getenv("SUDO_USER")
    if sudo_user is None: sudo_user = "root"
    
    return os.path.join(os.path.expanduser("~{}".format(sudo_user)), path)
    
